import { useState } from "react";
import axios from "axios";
import Spinner from "./Loader";
const Cookies = require("js-cookie");
var CryptoJS = require("crypto-js");

export default function Search() {
  const [keyword, editKeyword] = useState();
  const [tooMany, setTooMany] = useState(false);
  const [wordTooMany, setWordTooMany] = useState(false);
  const [loading, setLoading] = useState(false);

  async function sub() {
    setTooMany(false);
    setWordTooMany(false);
    let cookieExists = get();
    if (cookieExists == false) {
      let ciphertext = CryptoJS.AES.encrypt(keyword, "//secr/k!!");
      let ciphernum = CryptoJS.AES.encrypt("1", "//secr/k!!");
      Cookies.set("num", ciphernum, { expires: 0.02083333333 });
      Cookies.set("wrds", ciphertext, { expires: 0.02083333333 });
      console.log("fetching");
      let formattedKw = keyword.replace(" ", "+");
      let getStr = "http://localhost:4000/keyword/" + formattedKw;
      setLoading(true);
      axios.get(getStr).then(response => {
        console.log(response);
        setLoading(false);
      });
    } else {
      let kwArray = cookieExists.split("+");
      console.log(kwArray);
      let newText = cookieExists + "+" + keyword;
      let ciphertext = CryptoJS.AES.encrypt(newText, "//secr/k!!");
      Cookies.set("wrds", ciphertext, { expires: 0.02083333333 });
      for (let i = 0; i < kwArray.length; i++) {
        if (keyword == kwArray[i]) {
          console.log("word found");
          setWordTooMany(true);
          return;
        }
      }
      let numCookie = getNumCookie();
      numCookie = parseInt(numCookie);
      if (numCookie == 5) {
        setTooMany(true);
        return;
      }
      numCookie += 1;
      let numtext = CryptoJS.AES.encrypt(numCookie.toString(), "//secr/k!!");
      Cookies.set("num", numtext, { expires: 0.02083333333 });
      console.log("found kwArray and fetching");
      setLoading(true);
      let formattedKw = keyword.replace(" ", "+");
      let getStr = "http://localhost:4000/keyword/" + formattedKw;
      axios.get(getStr).then(response => {
        setLoading(false);
        console.log(response);
      });
    }
  }

  function getNumCookie() {
    let cook = Cookies.get("num");
    if (cook == undefined) {
      let numtext = CryptoJS.AES.encrypt("1", "//secr/k!!");
      Cookies.set("num", numtext, { expires: 0.02083333333 });
      return "1";
    }
    var bytes = CryptoJS.AES.decrypt(cook.toString(), "//secr/k!!");
    var plaintext = bytes.toString(CryptoJS.enc.Utf8);
    console.log(plaintext);
    return plaintext;
  }

  function get() {
    let text = Cookies.get("wrds");
    if (text == undefined) {
      return false;
    } else {
      var bytes = CryptoJS.AES.decrypt(text.toString(), "//secr/k!!");
      var plaintext = bytes.toString(CryptoJS.enc.Utf8);
      console.log(plaintext);
      return plaintext;
    }
  }

  async function decrypt(text) {
    var bytes = CryptoJS.AES.decrypt(text.toString(), "//secr/k!!");
    var plaintext = bytes.toString(CryptoJS.enc.Utf8);
    return plaintext;
  }
  if (!loading) {
    return (
      <div className="outer-search-wrap">
        <div className="inner-search-wrap">
          <div className="search-title">
            <strong>enter your keyword</strong>
          </div>
          <div className="searchbar-wrap">
            <input
              className="keyword-input"
              type="text"
              onChange={e => editKeyword(e.target.value)}
            />
            <button className="search-button" onClick={sub}>
              Get Metrics
            </button>
          </div>
          <style jsx>
            {`
              div.inner-search-wrap {
                display: flex;
                flex-direction: column;
                padding: 15px;
              }
              div.outer-search-wrap {
                padding: 10px;
                display: flex;
                flex-direction: column;
              }
              div.search-title {
                margin: auto;
                margin-bottom: 10px;
              }
              button.search-button {
                border-radius: 0;
                border: none;
                padding: 8px;
                font-size: 10px;
                background-color: #e22b2e;
                color: #fff;
                text-transform: uppercase;
                letter-spacing: 1.5px;
                font-weight: 700;
              }
              .search-button:hover {
                background: #fff;
                color: #000;
                box-shadow: 0 12px 60px 0 #e22b2e;
              }
              div.searchbar-wrap {
                display: flex;
                flex-direction: row;
              }
              input.keyword-input {
                font-size: 13px;
              }
            `}
          </style>
        </div>
      </div>
    );
  } else if (!!loading) {
    return (
      <div className="outer-search-wrap">
        <div className="inner-search-wrap">
          <div className="search-title">
            <strong>
              fetching data for top 50 results...
              <br /> this could take a minute
            </strong>
          </div>
          <div className="loading-wrap">
            <Spinner />
          </div>
          <style jsx>
            {`
              div.inner-search-wrap {
                display: flex;
                flex-direction: column;
                padding: 15px;
              }
              div.outer-search-wrap {
                padding: 10px;
                display: flex;
                flex-direction: column;
              }
              div.search-title {
                margin: auto;
                margin-bottom: 10px;
                text-align: center;
              }
              button.search-button {
                border-radius: 0;
                border: none;
                padding: 8px;
                font-size: 10px;
                background-color: #e22b2e;
                color: #fff;
                text-transform: uppercase;
                letter-spacing: 1.5px;
                font-weight: 700;
              }
              .search-button:hover {
                background: #fff;
                color: #000;
                box-shadow: 0 12px 60px 0 #e22b2e;
              }
              div.loading-wrap {
                display: flex;
                flex-direction: row;
              }
              input.keyword-input {
                font-size: 13px;
              }
            `}
          </style>
        </div>
      </div>
    );
  }
}
